sqlMap.select:
select count(*) from (select a.cpid, a.cpname, a.startdate, a.enddate, case when ifnull(runcnt ,0) > 0 then 3 when ifnull(bookcnt ,0) > 0 then 2 when ifnull(readycnt ,0) > 0 then 1 when ifnull(completecnt ,0) > 0 then 5 when ifnull(pausecnt ,0) > 0 then 0 when ifnull(stopcnt ,0) > 0 then -1 when ifnull(cancelcnt ,0) > 0 then -2 when ifnull(totcnt ,0) = 0 then -3 else 0 end cp_state, a.clientid, c.corpname as clientname, a.agencyid, g.corpname as agencyname, a.medrepid, r.corpname as medrepname, a.tcid, a.budget, a.insertdate, a.updatedate, a.insertuser, a.updateuser, u.username as updateusername from campaign a left join (
) b on a.cpid = b.cpid left join corporation c on a.clientid = c.corpid and c.corptype = 1 left join corporation g on a.agencyid = g.corpid and g.corptype = 2 left join corporation r on a.medrepid = r.corpid and r.corptype = 3 left join users t on a.tcid = t.userid left join users u on a.updateuser = u.userid where a.stat = 1 a.startdate >= $start$ a.enddate <= $end$ ) x
cp_state = $cp_state$ $sch_column$ like '%$sch_text$%'

select a.*, st.isname as ads_statename, st.text, u.username as updateusername from (select adsid, a.adsname, b.cpname, b.clientid, c.corpname as clientname, b.agencyid, g.corpname as agencyname, b.medrepid, r.corpname as medrepname, a.startdate, a.start_hour, a.start_min, a.enddate, a.end_hour, a.end_min, a.goaltype, a.period, a.salestype, a.prtype, a.budget, a.book_total, a.goal_total, a.goal_daily, a.ads_state, a.insertuser, a.insertdate, a.updateuser, a.updatedate from adsrv.ads a left join adsrv.campaign b on a.cpid = b.cpid left join corporation c on b.clientid = c.corpid and c.corptype = 1 left join corporation g on b.agencyid = g.corpid and g.corptype = 2 left join corporation r on b.medrepid = r.corpid and r.corptype = 3 where a.stat = 1 ads_state = $ads_state$ a.startdate >= $start$ a.enddate <= $end$ ) a left join users u on a.updateuser = u.userid left join code_instance st on st.code = 'ads_state' and a.ads_state = st.isid
$sch_column$ like '%$sch_text$%'
limit $skip$,$max$ ;

select count(*) from (select adsid, a.adsname, b.cpname, b.clientid, c.corpname as clientname, b.agencyid, g.corpname as agencyname, b.medrepid, r.corpname as medrepname, a.startdate, a.start_hour, a.start_min, a.enddate, a.end_hour, a.end_min, a.goaltype, a.period, a.salestype, a.prtype, a.budget, a.book_total, a.goal_total, a.goal_daily, a.ads_state, a.insertuser, a.insertdate, a.updateuser, a.updatedate from adsrv.ads a left join adsrv.campaign b on a.cpid = b.cpid left join corporation c on b.clientid = c.corpid and c.corptype = 1 left join corporation g on b.agencyid = g.corpid and g.corptype = 2 left join corporation r on b.medrepid = r.corpid and r.corptype = 3 where a.stat = 1 ads_state = $ads_state$ a.startdate >= $start$ a.enddate <= $end$ ) a left join users u on a.updateuser = u.userid left join code_instance st on st.code = 'ads_state' and a.ads_state = st.isid
$sch_column$ like '%$sch_text$%'
limit $skip$,$max$ ;

sqlMap.update:
		update adsrv.target_category
		   set category = $category$
           where tid = $tid$;
